mapper.select:
SELECT DT_INDEX AS dtIndex, DT_NO AS dtNo, DT_TYPE AS dtType, DT_TITLE AS dtTitle, DT_MAIN_PHOTO AS dtMainPhoto, DT_WRITER_NO AS dtWriterNo, DT_SAMMARY AS dtSammary, DT_EXPLAIN AS dtExplain, DT_DATE AS dtDate, DT_SEE AS dtSee, DT_LIKE AS dtLike FROM (SELECT DIETTIP_TB.*, ROW_NUMBER() OVER(ORDER BY DT_DATE DESC) AS NUM FROM DIETTIP_TB
WHERE DT_TYPE = 1
WHERE DT_TYPE = 2
WHERE DT_TYPE = 3
WHERE DT_TYPE = 4
) WHERE NUM BETWEEN #{start} AND #{end}
