mapper.select:
SELECT COUNT(*) AS totalCount
FROM BOARD_POST_TBL WHERE
MB_INDEX = #{mbIndex}

SELECT *
FROM (SELECT
BOARD_POST_TBL.*,
ROW_NUMBER() OVER(ORDER BY
POST_DATETIME
DESC)
AS NUM FROM BOARD_POST_TBL
WHERE MB_INDEX = #{mbIndex}
) WHERE NUM BETWEEN
#{start} AND #{end}

SELECT COUNT(*) AS totalCount
FROM BOARD_COMMENT_TBL
WHERE MB_INDEX = #{mbIndex}

SELECT COUNT(*) AS totalCount
FROM BOARD_BOOKMARK_TBL
WHERE MB_INDEX = #{mbIndex}

SELECT COUNT(*) AS totalCount
FROM QUESTION_TBL
WHERE MB_INDEX = #{mbIndex}
